  // * Navbar.
  .navbar {
    c: 0 bd;
    box-shadow: bsh i;
    /// Feat-> Theme navbar color.
    if ( navbar_c == 1 ) { c: 0 0 bg }
    /// Feat -> Show 'next' badge.
    if ( navbar_n != 1 ) { .canary-badge { display: none i }}
    .canary-badge .badge.badge-pill { c: white green green }
    /// Feat -> Shrink navigation.
    if ( navbar_s == 1 ) {
      > .container-fluid { max-width: calc(var(--width) - 32px) }
      /// Reset logo margin.
      .header-content .title a { margin-left: 0 i }
      /// Shrink search bar.
      .search form { width: 240px }
    }
    // Logo text color.
    .logo-text svg, svg.nav-logo .wordmark { v: fg }
    // Search.
    .search form {
      padding: 3px 2px 2px;
