    }
    // Navbar links and buttons.
    a, button {
      c: cm;
      transition: ts;
      &:hover, &:focus { c: fg 0 hl }
    }
    // Active tab/button/menu.
    &-sub-nav, &-nav {
      > li.active, .dropdown.show {
        > a, > button { c: fg 0 bd }
        svg { v: fg }
      }
      .btn:focus { c: 0 0 bd }
    }
    /// Fix the 'more' dropdown menu.
    .header-more {
      li:hover a { c: cm 0 t }
      li > a:hover { c: fg 0 hl }
    }
    // Separator.
    &-toggler {
      margin: 4px -7px 4px 0 i;
      border-left-color: bd i;
    }
    // ? Avatars -> Reset.
    .header-user-avatar {
      radius: 50%;
      border: none i;
      c: cm 0 t;
    }
    /// Embed theme version in the help menu.
    .header-help li:first-child {
      a { margin-top: 0.25rem }
      &::before {
        content: '🌚 ' var(--note);
        display: block;
        padding: 4px 12px 6px;
        border-bottom: 1px solid bd;
        color: fg;
      }
    }
  }

  // Navbar -> Projects and groups dropdown.
  .frequent-items-dropdown {
    &-container { height: 350px }
    &-menu {
      rad: r;
      .section-header { c: fg }
      .section-empty, [class *= 'namespace'] { c: cm }
      [class *= 'dropdown-sidebar'] { c: 0 bd }
    }
  }

  // * Dropdown.
  .dropdown {
    // ? Toggles.
    &-toggle, &-menu-toggle {
      transition: ts;
      &:not(.search-input):not([class *= 'btn-']) {
        c: cm bd bg;
        &:hover { c: fg 0 hl }
      }
      // ? Add missing top margin to the dropdown on group page. @upstream
      + ul.show { margin-top: 10px i }
      // ? Active state for toggles.
      ^[0].show:not(#z) & {
        c: fg bd bd;
        button[aria-expanded = 'true'] { c: fg 0 bd }
        ^[2] ^[0]-toggle-text { c: fg }
        // Navbar -> Fix search input.
        &#search { c: 0 0 t }
      }
      // Active state for non-toggle buttons.
      ^[0].show:not(.commit-stat-summary) {
        > button[aria-expanded = 'true'] { c: fg 0 bd }
      }
    }
    // Dashboard -> Dropdowns in activities.
    &-toggle-text.is-default { c: cm }
    // On event.
    &-item {
      transition: ts;
      &:hover, &:focus { c: fg 0 hl 0 0 0 }
    }
    // Shadow.
    &-content-faded-mask::after { background: none }
    // Color boxes.
    &-label-box { border-color: inherit i }
    // Button groups.
    .btn-group {
      > button, > a {
        c: 0 bd bg;
        transition: ts i;
        &:hover, &.active { c: 0 0 hl }
        &:first-child { border-radius: r 0 0 r }
        &:last-child { border-radius: 0 r r 0 }
      }
    }
    a:active { c: fg 0 bd }
    .bold { c: cm }
    label { c: fg }
    // ? Navbar -> Search menu.
    &-select {
      li {
        &.separator { c: 0 0 bd }
        &.dropdown-header { c: cm }
        a {
          span b { c: ac }
          &:hover, &.is-focused { c: fg 0 hl }
          // ? Multiple `active` elements.
          &.is-active { c: cm }
        }
      }
    }
    // ? Issue boards -> Switch board.
    &-extended-height {
      a {
        c: cm;
        transition: ts;
        &:hover, &:active, &:focus { c: fg 0 hl }
        &.is-active { c: ac }
      }
      // ? Boards -> Create new board.
      .dropdown-divider ~ .dropdown-item {
        c: cm;
        transition: ts;
        &:hover, &:active, &:focus { c: fg 0 hl }
      }
    }
    // ? Project -> Insights.
    &.js-insights-dropdown {
      padding-left: 0;
      button.dropdown-toggle {
        box-shadow: none i;
        &:focus, ../.show .dropdown-toggle { c: fg bd bd }
      }
    }
  }
  .dropdown-menu {
    rad: r;
    c: 0 bd bg;
    box-shadow: 0 4px 8px 0 sh;
    // ? Height reset.
    &-selectable.show { max-height: unset i }
    &, &-nav { max-height: 352px i }
    // ? Header/Title.
    .dropdown-header, .dropdown-title { c: fg bd }
    .dropdown-bold-header, &-inner-title { c: fg }
    &-inner-content { c: cm }
    // ? Input / Search.
    .dropdown-input-field, .default-dropdown-input {
      rad: r;
      c: fg bd bg;
      &:focus {
        c: 0 ac;
        box-shadow: bsh;
      }
    }
    // ? Body/Content.
    .dropdown-info-note { c: fg }
    [class *= 'divider'] { c: 0 bd t }
    .dropdown-item {
      c: cm;
      &:focus, &:active { c: 0 0 bd }
      // ? Search projects on `/-/operations` page.
      &:not(.disable-hover):hover { c: 0 0 hl }
    }
    li {
      // ? Reset avatar padding.
      .dropdown-menu-user-link:not(.is-active) { padding-left: 8px }
      // ? Base styles.
      &:hover a, &:hover button:not(.btn-success) { c: fg 0 hl }
      a, button:not(.btn) {
        c: cm;
        transition: ts;
      }
      /// #92
      .menu-item {
        c: cm 0 t;
        transition: ts;
        &:hover { c: fg 0 bd }
      }
      .btn-default { c: cm }
      .is-focused, a:focus, a:active { c: fg 0 bd }
      &.droplab-item-active .btn { c: fg 0 bd }
    }
    // ? MR -> 'Created date' dropdown.
    &-sort {
      li {
        // ? Reset due to markup being single `li` with a ton of `a` elements.
        &:hover a { c: cm 0 t }
        a {
          transition: ts;
          &:hover { c: fg 0 hl }
          &.is-active { c: fg }
        }
      }
    }
    // ? MR -> Download button.
    &-right:not(&-sort) {
      li {
        // ? Reset due to markup being single `li` with a ton of `a` elements.
        &:hover a { c: cm 0 t }
        a, button:not(.btn), .menu-item {
          c: cm;
          transition: ts;
          &:hover { c: fg 0 hl }
        }
        .form-group label { c: fg }
      }
    }
    // ? Project -> Notifications.
    &-large {
      li a { transition: ts }
    }
    // ? Snippets -> Embed menu.
    &-selectable {
      li {
        button { c: cm }
        .is-active { c: fg }
      }
    }
    // ? Issue boards -> Add list.
    &-tabs {
      // max-height: 400px i;
      .dropdown-tabs-list {
        c: 0 0 bg;
        box-shadow: 0 1px 0 0 bd i;
        .dropdown-tab-item {
          c: 0 bd;
          a { c: fg 0 bg }
          a.active { c: cm ac bg }
        }
      }
    }
    // ? Diff -> File finder.
    &.diff-file-changes {
      min-width: 500px i;
      .diff-changed-file {
        [class *= 'path'] { c: cm }
        &.is-focused, &:focus {
          c: 0 0 bd;
          [class *= 'file-name'] { c: fg }
        }
      }
    }
    // ? Footer.
    .dropdown-footer {
      c: 0 bd;
      li:hover a { c: cm 0 t }
      li a {
        transition: ts;
        &:hover { c: fg 0 hl }
      }
    }
    // ? Loader.
    [class *= '-loading'] {
      c: 0 0 bg;
      .fa-spinner:before { color: cm }
    }
    &.big-pipeline-graph-dropdown-menu {
      &::after { c: 0 t }
      li:hover a { c: cm 0 t }
      .ci-job-component:hover > a { c: fg 0 hl }
    }
  }

  // * Dropdown -> The select2 component.
  .select2 {
    // Menu.
    &-drop {
      rad: r;
      c: 0 bd bg;
    }
    // Menu arrow.
    &-arrow {
      padding-left: 12px;
      c: 0 0 t;
      &::after { c: cm }
    }
    // Input/select.
    &-drop input, &-choice, &-search-choices, &-default {
      rad: r;
      c: cm bd bg;
    }
    &-choice:hover { c: 0 0 hl }
    &-container-multi &-choices {
      rad: r;
      c: cm bd bg;
