  // Global -> GFM links.
  .gfm {
    c: fg;
    &&-issue, &&-merge_request, &&-epic, &&-milestone { c: ac }
    &&-commit {
      &, &_range {
        font-family: var(--ui-font);
        font-size: 100%;
      }
      &, &_range > code { c: ac }
    }
    &&-project_member {
      z-index: 1;
