      text-decoration: none;
      transition: 0.2s ease;
      font-weight: 500;
      c: ac 0 t;
      &::before {
        z-index: -1;
        content: '';
        opacity: 0.2;
        position: absolute;
        top: 0; right: 0; bottom: 0; left: 0;
        transition: 0.2s ease;
        rad: inherit;
        c: 0 0 ac;
      }
      &:hover::before { opacity: 0.4 }
      &.current-user {
        c: white 0 ac;
        &:hover { opacity: 0.7 }
      }
    }
    /// Color chips. #71
    &-color_chip > span { c: 0 t }
    /// Labels. #103
    /.gl-label {
        c: 0 0 hl;
        &-label-link { c: 0 0 t }
        &-text&-text-light { text-shadow: 1px 1px 0 sh }
        &-link, ../-label { &:not(#z) { color: inherit i }}
    }
  }

  // * Dashboard -> Search page hacks.
  [data-page ^= 'search:'] {
    // Fix search button.
    .btn-search {
      c: white green green;
      // Tweak text.
