  // * Alerts.
  .alert-wrapper {
    display: flex;
    flex-direction: column;
    // Move breadcrumbs to the last spot.
    > nav { order: 999 }
    // Alert main reset.
    [class *= 'flash-'] {
      margin-bottom: 0px i;
