      &-warning { c: white orange orange }
      &-primary { c: blue blue hl }
      &-link { c: white }
    }
    // ? Broadcasts found on `git.synz.io`.
    .broadcast-message {
      p, .fa { color: inherit i }
      &[style *= 'd-color: #004275'] { c: white 0 blue }
      &[style *= 'd-color: #00bd7f'] { c: white 0 green }
      &[style *= 'd-color: #e75e40'] { c: white 0 orange }
      &[style *= 'd-color: #f4f4f4'] { c: fg 0 bd }
