    rad: r;
    c: 0 0 bg;
    box-shadow: inner, bsh;
    .tile-info { c: 0 0 t }
  }

  // * Articles.
  .blog.article {
    .header {
      .author a:hover { &, i { c: ac }}
      h1, p { c: white }
    }
    .overlay { background: linear-gradient(0deg, bg, t 100%) }
    .body { c: 0 0 bg }
    // Anchors.
    .content {
      blockquote { c: cm bd }
      .badge:not(.available) {
        c: fg;
        box-shadow: none i;
      }
      a:not(.twitter-block-link):not(.btn) { c: ac }
      .highlight {
        rad: r;
        c: fg bd hl;
        > code { c: 0 t t }
        .err { c: white 0 red }
        // Syntax highlighting.
        .na, .c, .c1 { c: cm }
        .n, .p, .nb { c: fg }
        .gr, .nf, .pi, .si { c: red }
        .nn, .na { c: cm }
        .s1, .s2 { c: green }
        .no, .kp, .k { c: violet }
        .nc, .nt { c: blue }
        .se { c: orange }
        .vi, .o, .s, .ss { c: cyan }
        .gi { c: 0 0 green }
      }
      :not(pre) > code {
        c: 0 0 hl;
        box-shadow: inner;
      }
    }
    .btn {
      &-red:not(#z) { c: white }
      &-default { c: fg bd }
    }
    // Availability of the features.
    .badge:not(.available) { c: fg bd hl }
    .top-row .badge.available { c: white red red }
    .bottom-row .badge.available { c: white orange orange }
    // Previous/next buttons.
    .pull-left a, .pull-right a { c: fg bd bg }
    // Signup section.
    .signup {
      c: 0 0 t;
      > .panel { border: 1px solid bd }
    }
    // Disqus section.
    .comments {
      rad: r;
      border: 1px solid bd;
      c: 0 0 bg;
    }
    // Article tags.
    .tag {
      c: 0 0 hl;
      a { c: cm }
      a:hover { c: fg }
    }
  }
  // Header anchor.
  .header-link > i.fa-link { c: cm }
  // Divider between new features.
  [class $= '-column']:last-child { c: 0 bd }

  // Try GitLab.
  .sticky-banner {
    c: 0 0 bg;
    box-shadow: 0 -1px bd;
    a:not(.btn) { c: ac }
  }

  .gray-section { c: cm bd bg }
  .blog-category-nav ul li {
    c: cm;
    &::after { c: 0 0 ac }
    &:hover { c: ac }
  }

  // * Navbar.
  .navbar {
    a.navbar-brand { c: 0 0 t }
    // Transparentize header.
    &.navbar-header-transparent {
      c: 0 0 t;
      box-shadow: none i;
    }
    // Search.
    .search-box {
      background: none i;
      c: fg 0 bg;
      box-shadow: inset 0 -1px bd;
      &-large, .search-input { c: fg t t }
    }
    .sign-up, .search-icon {
      rad: r;
      c: 0 0 t;
      &:hover { c: ac 0 bg }
    }
    .icon-bar { c: 0 0 cm }
    // Mobile.
    .mobile-menu {
      c: 0 0 bg;
      li .dropdown a {
        rad: r;
        &:hover { c: 0 0 bd }
      }
      li .main-nav-link { c: ac }
      ul .sign-up-container {
        c: 0 0 bg;
        li .sign-up {
          rad: r;
          c: cm 0 bd;
          &:hover { c: fg }
        }
      }
    }
    // Dropdowns and items.
    .navbar-right ul li {
      a, .search-icon i {
        rad: r;
        c: fg;
        &:hover:not(.fa), &:focus { c: fg 0 bg }
      }
      .dropdown {
        rad: r;
        border: 1px solid;
        c: cm bd bg;
        a {
          rad: r;
          c: ac;
          &:hover { c: ac 0 hl }
        }
      }
      // Active page indicator.
      &.menu-item-active::after { c: 0 0 ac }
    }
  }

  // Announcements.
  .announcement-container {
    rad: r;
    c: cm 0 bg;
    box-shadow: inner;
    img { filter: grayscale(100%) }
  }

  // ? https://about.gitlab.com/releases
  .simple-header, .blank-header {
    z-index: 1;
    &, &::after {
      background-image: linear-gradient(60deg, orange -100%, violet 200%);
      background-color: bg;
    }
    h1 { text-shadow: 2.5px 2.5px 0 sh }
    // Prevent custom opacity on these images.
    img {
      pointer-events: none;
      &.hero-image-small {
        opacity: 1 i;
        filter: drop-shadow(0 4px 4px sh);
      }
    }
  }

  // * Main content.
  .md-page {
    rad: r;
    c: 0 0 bg;
    box-shadow: inner, 0 3px 3px 0 sh;
    // Inputs.
    input, select, textarea, fieldset {
      rad: r;
      c: fg bd bg;
    }
    .highlight {
      c: fg bd bg;
      > code { c: 0 t t }
      .err { c: white 0 red }
    }
    li {
      c: cm;
      a { c: ac }
      a code { c: ac }
    }
    p.date { font-weight: bold }
    .breadcrumb { c: 0 0 bd }
    .breadcrumb > li + li::before { c: cm }
  }

  // ? https://about.gitlab.com/press/press-kit
  .tile {
    rad: r;
    c: 0 0 bg;
    transition: ts;
    box-shadow: inner, bsh;
    &:hover { box-shadow: inner, 0 4px 8px 2px sh }
    .list { c: 0 bd }
    .list > a { c: 0 bd }
  }
  // Article borders.
  .press-block { c: 0 bd }
  // Mini nav.
  .press-toc-links, .toc-links {
    a { c: fg }
    a:hover { c: ac t }
  }

  // ? https://about.gitlab.com/solutions/public-sector
  .summary-container, .characteristics-container { c: 0 bd bg }
  .form-container {
    margin-bottom: -80px;
    c: 0 0 bg;
  }

  // ? https://about.gitlab.com/pricing/
  .tabs-wrapper {
    c: 0 bd;
    .pricing-tabs {
      li { c: 0 bd bg }
      li.active { c: 0 bd bd }
    }
  }
  // Plans.
  .plan-row .plan {
    c: cm orange bg;
    box-shadow: inner;
    li > a { color: ac }
    .btn-pricing { c: ac ac }
    .plan-name, .all-features { c: fg bd }
  }

  // QnA.
  .faq {
    margin-bottom: -80px;
    c: 0 bd bg;
    .faq-item {
      .faq-inner-item { c: cm bd }
      &.is-open .faq-inner-item {
        c: fg bd;
        .faq-answer p { c: 0 0 bd }
      }
    }
  }

  // Sign up.
  .mktoForm {
    input[type], .mktoField { c: fg bd bg }
  }
  .newsletter-form .mktoButton {
    c: fg sh bg;
    &:hover { c: ac sh bg }
  }
  .newsletter-form .mktoForm .mktoLogicalField label { c: fg }

  // Page -> Case studies.
  .case-study-page {
    .customer-info-section-wrapper { c: 0 bd }
    .customer-case-section-wrapper { c: 0 0 bg }
  }

  // Page -> Jobs.
  .jobs-content-row {
    c: 0 0 bg;
    // OpenStreetMap reset.
    + div .teleport-thing {
      margin-top: 20px;
      iframe { rad: r }
    }
  }

  // Front page
  .sdlc-container .sdlc-table {
    td:not(:last-child), td:not(:last-child), tr:not(:last-child) {
      border-color: bd i;
    }
    .competitor-logo-container {
      background-color: bd i;
    }
  }

  // Cards.
  .gke-layer .gke-layer-body, .gke-layer .k8s-layer-media,
  .testimonials-container .testimonials-row .testimonial, .forrester-graphic-title,
  .feature, .integrated-teams {
    border: 1px solid;
    rad: r i;
    c: fg bd bg;
    box-shadow: bsh;
  }
  .k8s-layer-media { margin-left: 16px }
  .u-subtle-bg {
    background-color: bg;
  }
  .flex-tile-list > .tile {
    c: fg bd bd;
  }
  .cta-btn {
    transition: ts;
    rad: r;
    &.orange, &.orange-light {
      c: white orange orange;
      &:hover { opacity: 0.8 }
    }
    &.accent {
      c: white ac ac;
      &:hover { opacity: 0.8 }
    }
  }
  .panel {
    rad: r;
    c: cm bd hl;
    box-shadow: bsh;
    &-body { c: cm }
    &&-newsletter-signup {
      border: 1px solid bd;
      padding: 16px;
    }
  }
  .panel-info > .panel-heading {
    c: fg bd bd;
  }
  .panel-success > .panel-heading {
    c: cm green green;
    .panel-title { c: cm }
  }
  .panel-warning > .panel-heading {
    c: cm orange orange;
    .panel-title { c: cm }
  }
  // Featured Posts.
  .blog.list {
    .article {
      rad: r;
      c: cm bd hl;
      box-shadow: inner;
      .date { c: cm }
      .author a { c: ac }
      .author a:hover i { c: ac }
      .cover {
        &, .overlay { rad: r r 0 0 }
        .overlay { background: linear-gradient(153deg, t, bd) }
      }
    }
    .more, .browse {
      c: cm bd bg;
      &:hover { c: fg 0 bd }
    }
  }

  .blog {
    &:not(.article) .wrapper { max-width: var(--width) }
    .callout, .gitlab-trial { c: cm bd hl }
  }

  // * Handbook search.
  .st-ui {
    &-autocomplete, &-slide-autocomplete {
      > div[class] {
        rad: r i;
        c: fg bd bg;
        box-shadow: bsh;
      }
    }
    &-result[href] {
      border-color: bd i;
      &:hover { c: fg bd bd }
      .st-ui-type-heading {
        c: ac;
        em {
          c: white 0 ac;
          box-shadow: inset 0 -1px ac;
        }
      }
      .st-ui-type-detail, .st-ui-type-detail-bold {
        c: cm;
        em {
          c: ac 0 t;
          box-shadow: inset 0 -1px ac;
        }
      }
    }
  }

  // Search
  .st-ui-injected-overlay-container {
    c: cm bd bg;
    .st-ui-header, .st-ui-footer {
      c: cm bd bg;
      input[type = 'text'] { c: cm }
    }
  }
  a.st-ui-close-button {
    filter: invert(1);
  }

  // Code
  .command, .alert-warning { c: ac sh bd }
  .panel-body pre { c: ac sh bg }
  .method p a:not(.btn) { c: ac }
  .distro-content .display-extra-links li {
    a {
      c: ac bd;
      background: t i;
      text-shadow: none i;
      text-decoration: none i;
    }
    &:not(:last-child)::before { c: 0 0 bd }
  }

  // Footer.
  .footer {
    margin-top: 50px;
    padding-top: 20px;
    border-top: 1px solid;
    c: 0 bd bg;
  }

  // Footer -> Sources.
  .source-link {
    c: cm 0 bg;
    box-shadow: 0 -1px bd;
    a[href] { c: ac ac }
    a[href]:hover { c: 0 t }
  }

  // * Feature -> Custom logo.
  if ( dark_logo == 1 ) {
    svg.nav-logo {
      filter: drop-shadow(0 2px 4px sh);
      path:nth-child(1) { v: fg 0 }
      path:nth-child(2), path:nth-child(5), path:nth-child(8) { v: bg 0 }
      path:nth-child(7), path:nth-child(4) { v: bg 0 }
      path:nth-child(3), path:nth-child(6) { v: ac 0 }
    }
  }

  // Applications -> Search.
  .search-container {
    border: 1px solid bd;
    rad: r;
    c: fg 0 bg;
    input { c: fg 0 bg }
    .suggestions {
      c: 0 0 bg;
      li { rad: r }
      .selected-suggestion, li:hover { c: 0 0 hl }
      .hl { c: 0 0 ac }
    }
  }
  // Categories.
  .category {
    &-name { c: fg 0 0 }
    &-applications .tile:hover { c: 0 0 hl }
  }
  // Scroll.
  .scroll-to-top { c: fg 0 hl }

  // Page: https://about.gitlab.com/events/commit/
  .commit-content {
    #learn, #sponsor { c: 0 0 t }
    .logos-container svg path { v: cm }
    .highlight-background { c: 0 0 hl }
    .dark-tabs {
      a { c: cm t }
      .active a, a:focus, a:hover { c: ac ac }
    }
  }
}

