@-moz-document regexp('https?://docs.gitlab.com(/.*)') {
  // * Landing page.
  .landing {
    // Search container.
    .landing-header {
      c: fg 0 bg;
      box-shadow: 0 1px 0 0 bd, 0 2px 8px 0 sh;
      // Navbar.
      &-top {
        c: 0 bd bg;
        box-shadow: 0 1px 0 0 bd, bsh;
        // Items.
        .header-link {
          transition: ts;
          text-decoration: none i;
          &:hover p { c: ac }
        }
      }
      // Search.
      .input-container {
        img { filter: invert(var(--bg-invert)) }
        input {
          border: 1px solid bd;
          rad: r;
          c: fg 0 hl;
          &:focus { box-shadow: 0 4px 16px -4px sh }
        }
      }
    }
    // Cards.
    .main-topics li {
      rad: r;
      border: 1px solid;
      c: 0 bd bg;
      box-shadow: 0 4px 16px -4px sh i;
    }
    // Most popular topics.
    .topic h4 { c: 0 bd }
  }

  // * Navigation.
  .header {
    c: 0 bd bg;
    // Search bar.
    #search-form {
      c: cm;
      // Found on docs/.../archives page.
      .docsearch {
        rad: r;
        c: cm 0 bd;
      }
      input[type = 'text'] {
        padding: 2px 11px 2px 28px;
        line-height: 30px;
        rad: r;
        c: fg bd;
      }
    }
    // Docs versions.
    .btn-versions { c: cm bd t }
    .versions-tooltip { c: fg 0 bg }
    // Main navigation.
    .nav-item a { c: fg }
    .nav-item:hover a { c: ac }
  }

  // Cards
  .main-topics li .wrapper h2 {
    border-color: bd i;
  }

  // Footer
  .footer {
    c: 0 0 bg;
    box-shadow: 0 -1px 0 0 bd, 0 -2px 4px 0 sh;
    // Colorize icons.
    a:hover .fa { c: ac }
  }

  // Logo
  if ( dark_logo == 1 ) {
    .landing .landing-header-top .logo, .header img {
      height: 0 i;
      width: 0 i;
      padding: 40px 0 0 40px i;
      background-image: url('https://gitlab.com/vednoc/dark-gitlab/raw/master/images/docs_logo.png') i;
      background-size: 40px 40px;
      background-repeat: no-repeat;
    }

    .footer-logo path.logo-dark-orange-shape:nth-child(4), .footer-logo path.logo-light-orange-shape:nth-child(3) {
      dark-logo: t, 0, 0, 0, 0;
    }
    .footer-logo path.logo-svg-shape:nth-child(1) {
      dark-logo: 0, t, 0, 0, 0;
    }
    .footer-logo path.logo-dark-orange-shape:nth-child(7), .footer-logo path.logo-light-orange-shape:nth-child(6) {
      dark-logo: 0, 0, t, 0, 0;
    }
    .footer-logo path.logo-orange-shape:nth-child(2) {
      dark-logo: 0, 0, 0, t, 0;
    }
    .footer-logo path.logo-orange-shape:nth-child(5) {
      dark-logo: 0, 0, 0, 0, t;
    }
  }

  // * Main content.
  .main {
    c: 0 bd;
    h1 { c: 0 bd }
    img { box-shadow: none i }
    * > a { c: ac }
    // Help and feedback section.
    .comments {
      padding: 16px;
      margin: 16px auto -16px auto;
      rad: r;
      border: 1px solid;
      c: 0 bd bg;
      .nav-tabs {
        margin: 0;
        c: 0 bd;
        li a {
          border-bottom: none;
          c: cm t t;
          &:first-child { rad: r 0 0 r }
          &.active { c: fg bd bd }
        }
      }
      // Buttons.
      .comments-filter a.comments-holder {
        c: 0 bd bg;
        box-shadow: none i;
      }
      .tab-content .row {
        margin-top: 16px;
        c: 0 bd;
      }
    }
  }
  .anchor {
    filter: invert(1);
  }
  .badge-small:hover::after {
    c: ac sh bd;
    box-shadow: bsh i;
  }

  // Alerts.
  .alert {
    &-info { c: blue }
    &-warning { c: orange orange hl }
    &-success { c: green 0 hl }
    &-danger { c: red 0 hl }
    &-gray { c: fg bd hl }
  }

  // * Left sidebar.
  .nav-wrapper {
    c: 0 bd bg;
    .global-nav {
      c: 0 0 t;
      &-cat.active a { c: ac }
      .collapse-toggle {
        c: 0 0 t;
        // transition: ts;
        &::after { c: 0 cm }
        &.active { c: 0 0 bd }
      }
      &-section.expanded {
        c: 0 0 t;
        box-shadow: inset 4px 0 0 ac;
      }
      a.active { c: cm ac bd }
      &-block-top a {
        color: fg;
        text-shadow: none;
      }
      &-link {
        color: cm;
        box-shadow: none;
        transition: 0s;
        &:hover, &:focus { &, + .collapse-toggle { c: fg 0 hl }}
        &.active[href] { c: ac }
      }
      .nav-link:hover { c: 0 0 hl }
    }
    // Collapse sidebar.
    .nav-toggle {
      c: 0 t t;
      box-shadow: 0 -1px bd i;
      &:hover { c: 0 0 hl }
      .label { c: fg }
      .arrow { &::before, &::after { c: 0 0 cm }}
    }
  }

  // * Right sidebar.
  .doc-nav {
    right: -12px;
    height: calc(100vh + 20px);
    c: cm 0 t;
    // Wrapper.
    #markdown-toc {
      a {
        text-decoration: none;
        c: cm;
        &:hover { c: fg }
        &.active { c: ac }
      }
    }
  }

  // * Search results page.
  // ? https://docs.gitlab.com/search/?q=test&idx=gitlab&p=1
  .instantsearch-input {
    .ais-search-box {
      c: 0 0 t;
      &--input {
        rad: r;
        c: fg bd bg;
        box-shadow: bsh;
        &:focus { c: 0 ac }
      }
      &--magnifier { pointer-events: none }
    }
    .search-results {
      .ais-stats {
        &, .stats {
          opacity: 1;
          c: cm;
        }
      }
      .ais-header {
        c: 0 bd;
        + .ais-body {
          label { c: fg }
          label > span { c: fg 0 bd }
        }
      }
      // Results.
      .hit {
        &-content {
          padding: 8px;
          font-size: 1rem;
          c: 0 t;
          box-shadow: none;
          em { c: ac 0 t }
        }
        &-text { c: cm }
        &-tag {
          rad: 0 0 3px 3px;
          c: fg t t;
        }
      }
      .ais-infinite-hits {
        rad: r;
        c: 0 0 bg;
        box-shadow: inner;
        &__empty { padding: 8px }
        &--item:not(:first-child) { box-shadow: 0 -1px bd }
        &--showmore > button {
          rad: r;
          border: 1px solid;
          c: cm bd bg;
        }
      }
    }
  }

  // * Code blocks.
  pre.highlight {
    rad: r;
    c: 0 bd hl;
    box-shadow: bsh;
    code {
      white-space: pre-wrap;
      word-break: break-all;
      rad: inherit;
      c: 0 t;
    }
    // Copy to clipboard.
    .clip-btn {
      top: 1px; right: 1px;
      rad: 0 r 0 0;
      c: cm 0 hl;
      .fa-clipboard::before { color: cm }
    }
    // Syntax highlighting.
    code, .na, .c, .c1 { c: cm }
    .n, .p, .nb { c: fg }
    .pi { c: red }
    .na { c: cm }
    .s1, .s2 { c: green }
    .no, .kp, .k { c: violet }
    .nt { c: blue }
    .se, .err { c: orange }
    .o, .s, .ss { c: cyan }
  }

  // Footer -> Sources.
  .source-link {
    c: cm 0 bg;
    box-shadow: 0 -1px bd;
    a[href] { c: ac }
  }
}

@-moz-document domain('status.gitlab.com') {
  // * Header.
  #statusio_header {
    margin: 0;
    display: flex;
    align-items: center;
    justify-content: space-between;
    // ? Make the header image smaller.
    #statuspage_header_image {
      float: left;
      width: 100%;
      max-width: 400px;
      &::before, &::after { clear: both }
    }
  }

  // * Status bar.
  [style *= 'd:#C44031'] { c: 0 0 red }
  [style *= 'd:#27AE60'] { c: 0 0 green }
  [style *= 'd:#00AAF0'] { c: 0 0 blue }
  [style *= 'd:#FFA837'] { c: 0 0 yellow }

  // * About section.
  .section_border_1 {
    c: 0 bd bg;
    a { c: ac }
    // Icons and text elements nested within them.
    .fa {
      span { c: fg }
      &-check { c: green }
    }
  }

  // * Table.
  .status_td { c: cm bd bg }

  // * History timeline.
  #statusio_history .history-filter-text, .back-text { c: cm }
  #statusio_history_timeline .timelineContainer {
    c: 0 bd;
    .incident_time { c: fg }
    .timelineMajor h2 {
      background-image: none i;
      &::after {
        content: '';
        position: absolute;
        margin-top: 20px;
        left: 16px;
        width: 24px;
        height: 2px;
        c: 0 0 bd;
      }
    }
    .timelineMinor dt {
      background: none;
      .open, .closed { c: cm }
      &::before {
        content: '';
        position: absolute;
        margin-top: 20px;
        left: 0px;
        width: 12px;
        height: 2px;
        c: 0 0 bd;
      }
    }
    hr { c: 0 0 bd }
  }

  // * Labels.
  .label-success { c: fg 0 green }
  .label-info { c: fg 0 blue }

  // * Rounded corners.
  .make_round {
    rad: r;
    &.make_round_bottom_only { rad: 0 0 r r }
    &.panel-heading { rad: r r 0 0 }
  }
  .component {
    &_first { rad: r r 0 0 }
    &_last { rad: 0 0 r r }
  }

  // * Footer.
  #bottom_menu {
    a { c: ac }
    strong a { text-decoration: underline }
  }
}

@-moz-document domain('next.gitlab.com') {
  body {
    background: none i;
    .wrapper > img.bg { opacity: 0 }
  }
  .white-box {
    rad: r;
    border: 1px solid;
    c: fg bd bg;
    a { c: ac }
    .text-switch { c: cm }
    .switch .cmn-toggle-round {
      + label { c: 0 0 bd }
      + label::after { c: 0 0 bd }
      + label::before { c: 0 0 bg }
      &:checked + label::before { c: 0 0 blue }
    }
  }
  .footer a { c: fg }
}

